mapper.select:
SELECT Mname, Mpeople
FROM socialmeeting
where mestablish >#{date}

select nvl( avg(cnt/#{people}),0)       
  from  (  
    select     names as names , cnt as cnt  , md  as md
       from  (      
     SELECT mname as names , count(mdate)as cnt  ,  mdate as md
      from socialmeetlist
      group by mname, mdate 
                   )
          where names=#{name}
   )

select genre
from member
where id=#{id}

select * from ( SELECT * FROM socialmeeting where mgenre in
#{item}
ORDER BY DBMS_RANDOM.RANDOM
) where rownum<=3

select * from ( SELECT * FROM ClassInfo where cgenre in
#{item}
ORDER BY DBMS_RANDOM.RANDOM
) where rownum<=3
